import React from 'react'

export const Home = () => {
  return (
    //   home page
    <section className='p-4 text-center tracking-wide'>
        {/* details */}
        <div className="">
            <p className='uppercase'>So, you want to travel to</p>
            <h1 className='text-6xl my-4 font-extralight text-white'>SPACE</h1>
            <p>Let’s face it; if you want to go to space, you might as well genuinely go to 
                outer space and not hover kind of on the edge of it. Well sit back, and relax
                because we’ll give you a truly out of this world experience!</p>
        </div>
        {/* image */}
        <div className="mt-32 bg-white px-6 w-fit mx-au">
            <button>EXPLORE</button>
        </div>
    </section>
  )
}
