import {useState, useEffect} from 'react';
import { crew1, crew2, crew3, crew4 } from '../data';
import { CrewMemberInfo } from './CrewMemberInfo';
import { CrewImage } from './CrewImage';
import { CrewCarousels } from './CrewCarousels';

export const Crew = () => {
  const [selected, setSelected] = useState("crew1");
  const [data, setData] = useState([]);
  const list = [
    {
      id: "crew1",
      title: "crew1"
    },
    {
      id: "crew2",
      title: "crew2"
    },
    {
      id: "crew3",
      title: "crew3"
    },
    {
      id: "",
      title: "crew4"
    },
  ]

  useEffect(() => {
    switch(selected){
      case "crew1" : 
        setData(crew1);
        break;
      case "crew2" :
        setData(crew2);
        break;
      case "crew3" :
        setData(crew3);
        break;
      case "crew4" :
        setData(crew4);
        break;
        default:
        setData(crew1)
    }
}, [selected])


  return (
    <section className='-mt-4 pt-[88px] font-Barlow-Condensed text-center bg-crew-bg-mobile md:bg-crew-bg-tablet lg:bg-crew-bg-desktop bg-no-repeat w-full bg-cover overflow-x-hidden h-screen'>
      <p className='tracking-[2.7px] text-white'><span className='opacity-25 text-white mr-[18px]'>02</span> MEET YOUR CREW</p>
      <div className="grid">
        <div className="w-[327px] h-[223px] mx-auto mt-8">
        {data.map(item => {
            return(
              <CrewImage 
                key={item.name}
                id={item.id}
                item={item}
              />
            )
          })}
          <hr className='border-[#383B4B] h-[1px] w-[327px] mx-auto' />
        </div>
        <section className='mt-8'>
          <ul className='uppercase text-sm tracking-[2.36px] flex justify-center items-center gap-[26px] my-8'>
          {
              list.map(item => {
                return(
                  <CrewCarousels 
                    id={item.id} 
                    active={selected === item.title} 
                    setSelected={setSelected} 
                    // title={item.title}
                  />
                )
              })
            }
          </ul>
          {
            data.map(item => {
              return(
                <CrewMemberInfo 
                  key={item.id}
                  id={item.id}
                  item={item}
                />
              )
            })
          }
        </section>
      </div>
    </section>
  )
}
