import Typical from 'react-typical';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { Contact } from "./Contact";
import cv from '../components/assets/Chibuike_Ukandu_ CV.pdf'
export const Home = () => {
  
  return (
    <Router>
      <div>
        <section className='bg-hero-img w-full h-screen bg-no-repeat bg-center bg-black bg-blend-multiply-300 bg-cover'>
          {/* text */}
          <div className="type-wrap w-11/12 mx-auto py-20 leading-loose">
            <h1 className='text-2xl text-white font-semibold mb-2'>Hello, I'm 
              <Typical 
                loop={Infinity}
                wrapper="b"
                steps={[
                  ' Chibuike Ukandu.',
                  10000,
                  ' a Software Developer.',
                  10000
                ]}
              />
            </h1>
            <h4 className='text-grey text-lg font-semibold'>I'm a developer who is passionate about creating interactive interfaces that solve human problems. </h4>
            {/* buttons */}
            <div class="">
              {/* contact */}
              <button>
                <Link to="">Hire Me</Link>
              </button>
              {/* GET CV  */}
              <button>
                <h4><a href={cv} download >Get CV</a></h4>
              </button>
              <Routes>
              <Route path="/contact" element={<Contact />} />
              </Routes>
              <Route path="/contact" element={<Contact />} />
            </div>
          </div>
        </section>
      </div>
    </Router>
    
  )
}
