import React from 'react';
import Data from './PortfolioData';
import { Card } from './PortfolioCard';

export const Portfolio = () => {
    const portfolioHeading = "<PORTFOLIO />";
    const cardBlock = Data.map(item => {
        return(
            <Card 
                key={item.id}
                item={item}
            />
        )
    })

    return (
        <div className='text-light-grey bg-portfolio-Bg overflow-x-hidden'>
            <div className="container px-8">
                <h1 className='text-center text-3xl sm:text-4xl font-semibold pt-8 pb-10'>{portfolioHeading}</h1>
                <p className='font-medium text-lg'>Below are my works</p>
                {/* buttons */}
                <section className="flex gap-x-8 gap-y-3 overflow-x-hidden flex-wrap w-[20rem] mx-auto mt-8">
                    <button className='bg-light-grey/5 px-4 py-1'>ALL</button>
                    <button className='bg-light-grey/5 px-4 py-1'>HTML</button>
                    <button className='bg-light-grey/5 px-4 py-1'>CSS</button>
                    <button className='bg-light-grey/5 px-4 py-1'>SCSS/SASS</button>
                    <button className='bg-light-grey/5 px-4 py-1'>BOOTSTRAP</button>
                    <button className='bg-light-grey/5 px-4 py-1'>TailwindCSS</button>
                    <button className='bg-light-grey/5 px-4 py-1'>Vanilla JS</button>
                    {/* <button className='bg-light-grey/5 px-4 py-1'>STYLED COMPONENTS</button> */}
                    <button className='bg-light-grey/5 px-4 py-1'>React JS</button>
                </section>

                <section>
                    {/* image */}
                    <div className=""></div>
                    {/* description */}
                    <div className={``}></div>
                    {/* {cardBlock} */}
                </section>
            </div>
            
        </div>
    )
}
