import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { useEffect, useState } from "react";
import logo from './components/assets/shared/logo.svg';
import hamburger from './components/assets/shared/icon-hamburger.svg';
import closeIcon from './components/assets/shared/icon-close.svg';
import { Home } from './Pages/Home';
import { Destination } from "./Pages/Destination";
import { Crew } from './Pages/Crew';
import { Technology } from './Pages/Technology';
import AOS from 'aos';
import 'aos/dist/aos.css';
// import { motion } from "framer-motion";

function App() {

  AOS.init()
  return (
    <Router>
      <div className="App bg-dark h-full">
        <nav className=''>
          <img src={logo} alt="logo" />
          {/* menu */}
          <div>
            {/* nav links */}
            <ul className="menu">
              <li id="home" className="relative text-light-grey text-xl hover:text-white" >
                <Link to="/"><span>00</span> Home </Link>
              </li>
              <li id="destination" className="relative text-light-grey text-xl hover:text-white">
                <Link to="/destination"><span>01</span> Destination </Link>
              </li>
              <li id="crew" className="relative text-light-grey text-xl hover:text-white">
                <Link to="/crew"><span>02</span> Crew</Link>
              </li>
              <li id="technology" className="relative text-light-grey text-xl hover:text-white">
                <Link to="/technology"><span>03</span> Technology</Link>
              </li>
            </ul>

            {/* close button */}
            <i className="ri-close-line sm:hidden text-light-grey hover:text-white text-6xl absolute top-6 right-4 sm:right-10 cursor-pointer"></i>
          </div>
          {/* hamburger */}
          <div className="flex justify-between flex-col lg:hidden gap-1.5 cursor-pointer p-3 rounded-md hover:bg-white">
            <span className="justify-self-start w-5 h-0.5 bg-light-grey"></span>
            <span className="w-8 h-0.5 bg-light-grey"></span>
            <span className="ml-auto mr-0 w-5 h-0.5 bg-light-grey"></span>
          </div>

        </nav>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/destination" element={<Destination />} />
          <Route path="/crew" element={<Crew />} />
          <Route path="/technology" element={<Technology />} />
        </Routes>

        {/* <footer className="bg-blackk text-light-grey pb-6 relative w-full bottom-0">
          <hr className="my-4 border-light-grey" />
          <div className="flex justify-between w-40 mx-auto xl:w-48">
            <a href="https://mobile.twitter.com/Chibuike035">
              <i className="ri-twitter-fill text-2xl sm:text-3xl hover:text-white"></i>
            </a>
            <a href="https://github.com/Jay035">
              <i className="ri-github-fill text-2xl sm:text-3xl hover:text-white"></i>
            </a>
            <a href="https://www.linkedin.com/mwlite/in/chibuike-ukandu-8385401b0">
              <i className="ri-linkedin-fill text-2xl sm:text-3xl hover:text-white"></i>
            </a>
            <a href="mailto:codexjay08@gmail.com">
              <i className="ri-mail-line text-2xl sm:text-3xl hover:text-white"></i>
            </a>
          </div>
        </footer> */}
      </div>
    </Router>
  );
}

export default App;
