import React from 'react'

export const Card = ({item}) => {
  return (
    <div className='my-20'>
        <a href="https://jay035.github.io/Calculator/" className='relative mx-auto'>
          {/* <!-- screenshot --> */}
          <img className='w-full h-[50%] mx-auto' src={item.image} alt="work screenshot" />
          {/* overlay */}
          <div className="bg-blackk/75 absolute top-0 h-full w-full hidden hover flex justify-center items-center">
              <h1 className='font-semibold text-white text-2xl'>{item.title}</h1>
          </div>
        </a>
        <div className="mt-4">
          <p className="text-xl font-semibold">{item.title} </p>
          {/* <p className="">{item.description}</p> */}
          <p className="text-base font-normal">{item.subTitle}</p>
          
          <div className="flex gap-6 mt-4">
            {/* <!-- link to github repo --> */}
            <a href={item.githubURL}>
                <button className='font-semibold rounded-lg w-24 py-3 bg-light-grey/5'>Github</button>                    
            </a>
            {/* <!-- link to live site  --> */}
            <a href={item.demoURL}>
                <button className='font-semibold rounded-lg w-24 py-3 bg-light-grey/5'>Demo</button>    
            </a>
          </div>
        </div>
    </div>
  )
}
