import React, { useState, useEffect } from 'react';
import { DestinationDescription } from './DestinationDescription';
import { DestinationList } from './DestinationList';
import { moon, mars, europa, titan } from '../data';
import { DestinationImage } from './DestinationImage';

export const Destination = () => {
  const [selected, setSelected] = useState("moon");
  const [data, setData] = useState([]);
  const list = [
    {
      id: "moon",
      title: "Moon"
    },
    {
      id: "mars",
      title: "Mars"
    },
    {
      id: "europa",
      title: "Europa"
    },
    {
      id: "titan",
      title: "Titan"
    },
  ]

  useEffect(() => {
    switch(selected){
      case "moon" : 
        setData(moon);
        // setImage(moon.images.png)
        break;
      case "mars" :
        setData(mars);
        break;
      case "europa" :
        setData(europa);
        break;
      case "titan" :
        setData(titan);
        break;
        default:
        setData(moon)
    }
}, [selected])

  return (
    <section className='-mt-4 pt-[88px] font-Barlow-Condensed text-center bg-destination-bg-mobile md:bg-destination-bg-tablet lg:bg-destination-bg-desktop bg-no-repeat w-full bg-cover overflow-x-hidden h-screen'>
      <p className='tracking-[2.7px] text-white'><span className='opacity-25 text-white mr-[18px]'>01</span> PICK YOUR DESTINATION</p>
      <div className="">
        {/* Destination image */}
        <div className="w-[170px] mx-auto mt-8">
          {data.map(item => {
            return(
              <DestinationImage 
                key={item.id}
                id={item.id}
                item={item}
              />
            )
          })}
        </div>
        <ul className='uppercase text-sm tracking-[2.36px] flex justify-center items-center gap-[26px] mt-[26px] mb-5'>
          {
            list.map(item => {
              return(
                <DestinationList 
                  key={item.id}
                  id={item.id} 
                  active={selected === item.id} 
                  setSelected={setSelected} 
                  title={item.title}
                  menuActive={item.active-line-width}
                />
              )
            })
          }
        </ul>
        {data.map(item => {
          return(
            <DestinationDescription
              key={item.id}
              id={item.id}
              item={item}
            />
          )
        })}
      </div>
    </section>
  )
}
