import React, {useState} from 'react';
import { NavLink } from 'react-router-dom';
import logo from './components/assets/shared/logo.svg';
import hamburger from './components/assets/shared/icon-hamburger.svg';
import closeIcon from './components/assets/shared/icon-close.svg'

export const Navbar = () => {
  const [menuDisplay, setMenuDisplay] = useState(false);

  return (
      <nav className='flex justify-between items-center pt-4 px-5'>
        <img src={logo} alt="logo" className='w-10' />
        {/* menu */}
        <div className={!menuDisplay ? `hidden -right-full lg:flex` : ` bg-white/10 backdrop-blur-md flex flex-col absolute top-0 right-0 w-[70%] lg:top-7 lg:right-10 h-screen lg:h-0 z-50 transition `}>
          {/* nav links */}
          <ul className="menu uppercase font-Barlow-Condensed flex flex-col order-2 mt-36 px-6 text-left justify-start lg:justify-end lg:flex-row font-medium text-base tracking-[2.7px] gap-6">
            <li id="home" className="relative px-2 w-fit transition-all after:absolute after:h-[2px] after:top-[30px] after:left-0 after:w-0 hover:after:w-full after:bg-white hover:text-white " >
              <NavLink className='font-medium' to="/"><span className='font-bold mr-2 text-white'>00</span> Home </NavLink>
            </li>
            <li id="destination" className="relative px-2 w-fit transition-all after:absolute after:h-[2px] after:top-[30px] after:left-0 after:w-0 hover:after:w-full after:bg-white hover:text-white">
              <NavLink className='font-medium' to="/destination"><span className='font-bold mr-2 text-white'>01</span> Destination </NavLink>
            </li>
            <li id="crew" className="relative px-2 w-fit transition-all after:absolute after:h-[2px] after:top-[30px] after:left-0 after:w-0 hover:after:w-full after:bg-white hover:text-white">
              <NavLink className='font-medium' to="/crew"><span className='font-bold mr-2 text-white'>02</span> Crew</NavLink>
            </li>
            <li id="technology" className="relative px-2 w-fit transition-all after:absolute after:h-[2px] after:top-[30px] after:left-0 after:w-0 hover:after:w-full after:bg-white hover:text-white">
              <NavLink className='font-medium' to="/technology"><span className='font-bold mr-2 text-white'>03</span> Technology</NavLink>
            </li>
          </ul>

          {/* close button */}
          <i onClick={() => setMenuDisplay(!menuDisplay)} className="ri-close-line absolute top-6 right-4 order-1 lg:hidden text-accent text-4xl cursor-pointer"></i>
        </div>
        {/* hamburger */}
        <div onClick={() => setMenuDisplay(prevValue => !prevValue)} className="flex justify-between flex-col gap-1.5 lg:hidden cursor-pointer rounded-md ">
          <span className="w-6 h-0.5 bg-accent"></span>
          <span className="w-6 h-0.5 bg-accent"></span>
          <span className="w-6 h-0.5 bg-accent"></span>
        </div>
      </nav>
  )
}
