import { useState, useEffect} from 'react';
import { terminology1, terminology2, terminology3 } from '../data';
import { TechnologyImage } from './TechnologyImage';
import { TechnologyList } from './TechnologyList';

export const Technology = () => {
  const [selected, setSelected] = useState("crew1");
  const [data, setData] = useState([]);
  const list = [
    {
      id: 1,
      title: "launch vehicle"
    },
    {
      id: 2,
      title: "spaceport"
    },
    {
      id: 3,
      title: "space capsule"
    }
  ]

  useEffect(() => {
    switch(selected){
      case "launch vehicle" :
        setData(terminology1);
        break;
      case "spaceport" :
        setData(terminology2);
        break;
      case "space capsule" :
        setData(terminology3);
        break;
        default:
        setData(terminology1)
    }
}, [selected])

  return (
    <section className='-mt-4 pt-[88px] transition-all font-Barlow-Condensed text-center bg-technology-bg-mobile md:bg-technology-bg-tablet lg:bg-technology-bg-desktop bg-no-repeat w-full bg-cover overflow-x-hidden h-screen'>
      <p className='tracking-[2.7px] text-white'><span className='opacity-25 text-white mr-[18px]'>03</span> SPACE LAUNCH 101</p>
      <div className="grid">
        <div className="w-[327px] h-[223px] mx-auto mt-8">
          {
            data.map(img => {
              return(
                <TechnologyImage  />
              )
            })
          }
        </div>
        <section>
        <ul>
        {
          list.map( item => {
            return(
              <TechnologyList 
                title={item.title}
                id={item.id}
              />
            )
          })
        }
      </ul>
        </section>
      </div>

    </section>
  )
}
