import { destinations } from '../data';

export const DestinationDescription = ({item}) => {
    return (
        <div className="">
            <h1 className='uppercase'>{ite.name}</h1>
            <p>{ite.description}</p>
            <hr />
            {/* Distance */}
            <div className="">
                <h3>AVG. DISTANCE</h3>
                <h1>{ite.distance}</h1>
            </div>
            {/* Travel time */}
            <div className="">
                <h3>EST. TRAVEL TIME</h3>
                <h1>{ite.travel}</h1>
            </div>
        </div>
    )
}
