import {useState, useEffect} from 'react';
import { crew } from '../data';
import { CrewImage } from './CrewImage';

export const Crew = () => {
  const [selected, setSelected] = useState("moon");
  const [data, setData] = useState([]);
  const list = [
    {
      id: "moon",
      title: "Moon"
    },
    {
      id: "mars",
      title: "Mars"
    },
    {
      id: "europa",
      title: "Europa"
    },
    {
      id: "titan",
      title: "Titan"
    },
  ]

  useEffect(() => {
    switch(selected){
      case "moon" : 
        setData(moon);
        break;
      case "mars" :
        setData(mars);
        break;
      case "europa" :
        setData(europa);
        break;
      case "titan" :
        setData(titan);
        break;
        default:
        setData(moon)
    }
}, [selected])


  return (
    <section className='-mt-4 pt-[88px] font-Barlow-Condensed text-center bg-crew-bg-mobile md:bg-crew-bg-tablet lg:bg-crew-bg-desktop bg-no-repeat w-full bg-cover overflow-x-hidden h-screen'>
      <p className='tracking-[2.7px] text-white'><span className='opacity-25 text-white mr-[18px]'>02</span> MEET YOUR CREW</p>
      <div className="grid">
        <div className="w-[170px] mx-auto mt-8">
        {data.map(item => {
            return(
              <CrewImage 
                key={item.id}
                id={item.id}
                item={item}
              />
            )
          })}
        </div>
        <ul className='uppercase text-sm tracking-[2.36px] flex justify-center items-center gap-[26px] mt-[26px] mb-5'>

        </ul>
      </div>
    </section>
  )
}
