import React, {useState} from 'react';
import { NavLink } from 'react-router-dom';
import logo from './components/assets/shared/logo.svg';
import hamburger from './components/assets/shared/icon-hamburger.svg';
import closeIcon from './components/assets/shared/icon-close.svg';

export const Navbar = () => {
  const [menuDisplay, setMenuDisplay] = useState(false);

  return (
    <nav className='flex justify-between items-center px-5 md:pr-0 bg-transparent absolute top-0 w-full z-50'>
      <img src={logo} alt="logo" className='w-10' />
      {/* menu */}
      <div className={`${!menuDisplay ? `hidden -right-full md:flex` :  `right-0 flex`} bg-white/10 backdrop-blur-lg flex-col absolute top-0 right-0 w-[70%] md:relative h-screen md:h-20 z-50 transition md:justify-center md:items-center`}>
        {/* nav links */}
        <ul className="menu uppercase font-Barlow-Condensed flex flex-col order-2 mt-36 px-6 md:px-4 text-left justify-start md:justify-end md:flex-row md:mt-0 md:py-6 font-medium text-base tracking-[2.7px] gap-6 md:gap-4">
          <li id="home" className="text-white relative px-2 w-fit transition-all after:absolute after:h-[2px] after:top-[30px] after:left-0 after:w-0 hover:after:w-full after:bg-white hover:text-white" onClick={() => setMenuDisplay(prevValue => !prevValue)} >
            <NavLink className='font-medium' to="/"><span className='font-bold mr-2 md:hidden'>00</span> Home </NavLink>
          </li>
          <li id="destination" className="text-white relative px-2 w-fit transition-all after:absolute after:h-[2px] after:top-[30px] after:left-0 after:w-0 hover:after:w-full after:bg-white hover:text-white" onClick={() => setMenuDisplay(prevValue => !prevValue)}>
            <NavLink className='font-medium' to="/destination"><span className='font-bold mr-2 md:hidden'>01</span> Destination </NavLink>
          </li>
          <li id="crew" className="text-white relative px-2 w-fit transition-all after:absolute after:h-[2px] after:top-[30px] after:left-0 after:w-0 hover:after:w-full after:bg-white hover:text-white" onClick={() => setMenuDisplay(prevValue => !prevValue)}>
            <NavLink className='font-medium' to="/crew"><span className='font-bold mr-2 md:hidden'>02</span> Crew</NavLink>
          </li>
          <li id="technology" className="text-white relative px-2 w-fit transition-all after:absolute after:h-[2px] after:top-[30px] after:left-0 after:w-0 hover:after:w-full after:bg-white hover:text-white" onClick={() => setMenuDisplay(prevValue => !prevValue)}>
            <NavLink className='font-medium' to="/technology"><span className='font-bold mr-2 md:hidden'>03</span> Technology</NavLink>
          </li>
        </ul>

        {/* close button */}
        <img src={closeIcon} onClick={() => setMenuDisplay(!menuDisplay)} className="absolute top-8 right-6 order-1 md:hidden text-accent w-6 cursor-pointer" alt='close button' />
      </div>
      {/* hamburger */}
      <div onClick={() => setMenuDisplay(prevValue => !prevValue)} className="flex justify-between flex-col gap-1.5 md:hidden cursor-pointer rounded-md ">
        <img src={hamburger} alt="hamburger" />
      </div>
    </nav>
  )
}
