import { Home } from "./pages/Home";
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { About } from "./pages/About";
import { Portfolio } from "./pages/Portfolio";
import { Contact } from "./pages/Contact";
import { ErrorPage } from "./pages/ErrorPage";
import logo from './components/assets/logo.png';

function App() {
  return (
    <Router>
      <div className="App">
        <nav className="bg-blackk flex justify-between items-center p-6">
          <div className="">
            <img className="w-32" src={logo} alt="logo" />
          </div>
          {/* menu */}
          <div className="fixed top-0 left-0 right-0 h-screen bg-blackk">

            {/* links */}
            <ul className="flex flex-col justify-center items-center text-xl text-center min-h-screen gap-3">
              {/* close button */}
              <Link className="text-light-grey" to="/">Home </Link>
              <Link className="text-light-grey" to="/about">About </Link>
              <Link className="text-light-grey" to="/portfolio">Portfolio </Link>
              <Link className="text-light-grey" to="/contact">Contact</Link>
            </ul>
            <i className="ri-close-line text-light-grey text-5xl absolute top-2 right-4 cursor-pointer"></i>

          </div>
          {/* hamburger */}
          <div className="flex justify-between flex-col gap-1.5 cursor-pointer">
            <span className="justify-self-start w-5 h-0.5 bg-white"></span>
            <span className="w-8 h-0.5 bg-white"></span>
            <span className="ml-auto mr-0 w-5 h-0.5 bg-white"></span>
          </div>

        </nav>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/portfolio" element={<Portfolio />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="*" element={<ErrorPage />} />
        </Routes>
        {/* <Home title="Chibuike Ukandu | CodexJay | Frontend Web Developer" /> */}
      </div>
    </Router>
  );
}

export default App;
