import { Home } from "./pages/Home";
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { About } from "./pages/About";
import { Portfolio } from "./pages/Portfolio";
import { Contact } from "./pages/Contact";
import { ErrorPage } from "./pages/ErrorPage";
import logo from './components/assets/logo.png';

function App() {
  return (
    <Router>
      <div className="App">
        <nav className="bg-blackk flex justify-between items-center p-6">
          <div className="">
            <img className="w-1" src={logo} alt="logo" />
          </div>
          <div className="hidden">
            <Link className="text-white" to="/">Home </Link>
            <Link className="text-white" to="/about">About </Link>
            <Link className="text-white" to="/portfolio">Portfolio </Link>
            <Link className="text-white" to="/contact">Contact</Link>
          </div>
          <div className="flex flex-col justify-center items-center gap-1 cursor-pointer">
            <span className="w-6 h-0.5 bg-white"></span>
            <span className="w-6 h-0.5 bg-white"></span>
            <span className="w-6 h-0.5 bg-white"></span>
          </div>

        </nav>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/portfolio" element={<Portfolio />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="*" element={<ErrorPage />} />
        </Routes>
        {/* <Home title="Chibuike Ukandu | CodexJay | Frontend Web Developer" /> */}
      </div>
    </Router>
  );
}

export default App;
