import React from 'react'

export const Card = ({item}) => {
  return (
    <div className='my-4 max-w-[450px]'>
        <a href={item.demoURL} className='relative mx-auto'>
            {/* <!-- screenshot --> */}
            <img className={`w-[35rem] object-cover mx-auto h-[16rem]`} src={item.image} alt="work screenshot" />
            {/* hover effect */}
            <div className="bg-blackk/75 absolute top-0 h-full w-full opacity-0 hover:opacity-100 flex justify-center items-center">
                <h1 className='font-semibold text-white text-2xl'>{item.title}</h1>
            </div>
        </a>
        <div className="mt-3">
            <p className="text-xl font-semibold">{item.title} </p>
            {/* <p className="">{item.description}</p> */}
            <p className="text-base font-normal">{item.subTitle}</p>
            
            <div className="flex gap-6 mt-4">
                {/* <!-- link to github repo --> */}
                <a href={item.githubURL}>
                    <button className='font-semibold rounded-lg w-24 py-3 bg-light-grey/20'>Github</button>                    
                </a>
                {/* <!-- link to live site  --> */}
                <a href={item.demoURL}>
                    <button className='font-semibold rounded-lg w-24 py-3 bg-light-grey/20'>Demo</button>    
                </a>
            </div>
        </div>
    </div>
  )
}
