import React, { useState, useEffect } from 'react';
import { DestinationDescription } from './DestinationDescription';
import { DestinationList } from './DestinationList';
import { destinations } from '../data';

export const Destination = () => {
  const [selected, setSelected] = useState("moon");
  const [data, setData] = useState([]);
  const list = [
    {
      id: "moon",
      title: "Moon"
    },
    {
      id: "mars",
      title: "Mars"
    },
    {
      id: "europa",
      title: "Europa"
    },
    {
      id: "titan",
      title: "Titan"
    },
  ]

  useEffect(() => {
    switch(selected){
      case "moon" : 
        setData(destinations[0]);
        break;
      case "mars" :
        setData(destinations[1]);
        break;
      case "europa" :
        setData(destinations[2]);
        break;
      case "titan" :
        setData(destinations[3]);
        break;
        default:
        setData(destinations[0])
    }
}, [selected])
  return (
    <section>
        <p><span>01</span> PICK YOUR DESTINATION</p>
        {/* Destination image */}
        <div className="">
          <img src={destinations.imag} alt="" />
        </div>
        <ul className='uppercase'>
          {list.map( item => {
            return(
              <DestinationList 
                key={item.id}
                id={item.id} 
                active={selected === item.id} 
                setSelected={setSelected} 
                title={item.title} 
              />
            )
          })}
        </ul>
        {data.map(item => {
          return(
            <DestinationDescription
              key={item.id}
              id={item.id}
              item={item}
            />
          )
        })}
    </section>
  )
}
