import React from 'react';
import Typical from 'react-typical';
import cv from '../components/assets/Chibuike_Ukandu_ CV.pdf'
export const Home = () => {
  
  return (
    <div>
        <section className='bg-hero-img w-full h-screen bg-no-repeat bg-center bg-b bg-blend-multiply-300 bg-cover'>
          {/* text */}
          <div className="text-center w-11/12 mx-auto py-20 leading-loose">
            <h1 className='text-2xl text-white font-semibold mb-2'>Hello, I'm 
              <Typical 
                loop={Infinity}
                wrapper="span"
                steps={[
                  ' Chibuike Ukandu.',
                  1000,
                  ' a Software Developer.',
                  1000
                ]}
              />
            </h1>
            <h4 className='text-grey text-lg font-semibold'>I'm a developer who is passionate about creating interactive interfaces that solve human problems. </h4>
            {/* buttons */}
            <div className="md:w-4/5 md:flex justify-center items-center gap-4 mt-12">
              {/* contact */}
              <button className="block bg-black-300 p-2 w-2/5 mx-auto my-2 text-white">
                <a className='py-4 px-8' href="/src/pages/Contact.js">Hire Me</a>
              </button>
              {/* GET CV  */}
              <button className="block bg-black-300 p-2 w-2/5 mx-auto my-2 text-white">
                <a className='p-4' href={cv} download >Get CV</a>
              </button>
            </div>
          </div>
        </section>
    </div>
  )
}
