import React from 'react';
import Typical from 'react-typical';
import cv from '../components/assets/Chibuike_Ukandu_ CV.pdf'
export const Home = () => {
  return (
    <div>
        <section className='bg-hero-img w-full h-screen bg-no-repeat bg-center bg-black-300 bg-blend-multiply bg-cover'>
          {/* text */}
          <div className="text-center w-11/12 mx-auto py-20 leading-loose">
            <h1 className='text-2xl text-white font-semibold mb-2'>Hello, I'm 
              <Typical 
                loop={Infinity}
                wrapper="span"
                steps={[
                  ' Chibuike Ukandu.',
                  1000,
                  ' a Software Developer.',
                  1000
                ]}
              />
            </h1>
            <h4 className='text-grey text-lg font-semibold'>I'm a developer who is passionate about creating interactive interfaces that solve human problems. </h4>
            {/* buttons */}
            <div className="md:w-4/5 md:flex justify-center items-center gap-12 mt-12">
              {/* contact */}
              <button className="block bg-button-black p-2 w-2/5 mx-auto my-2 text-white font-semibold">
                <a className='py-4 px-6 text-lg' href="/src/pages/Contact.js">Hire Me</a>
              </button>
              {/* GET CV  */}
              <button className="block bg-button-black p-2 w-2/5 mx-auto my-2 text-white font-semibold">
                <a className='py-4 px-6 text-lg' href={cv} download >Get CV</a>
              </button>
            </div>
          </div>
          <a href="#about-me" className='fi'>
            <svg className="arrow mt-12 w-fit mx-auto" xmlns="http://www.w3.org/2000/svg" width="38" height="38" fill="#a5a5a8" class="bi bi-arrow-down-circle-fill" viewBox="0 0 16 16">
              <path d="M16 8A8 8 0 1 1 0 8a8 8 0 0 1 16 0zM8.5 4.5a.5.5 0 0 0-1 0v5.793L5.354 8.146a.5.5 0 1 0-.708.708l3 3a.5.5 0 0 0 .708 0l3-3a.5.5 0 0 0-.708-.708L8.5 10.293V4.5z"/>
            </svg>
          </a>
        </section>
    </div>
  )
}
