import React from 'react'

export const Home = () => {
  return (
    //   home page
    <section className='md:grid-container border border-blue-500 gap-4 gap-x-8 p-4 pt-8 text-center md:text-left tracking-wide grid'>
        {/* details */}
        <div className="md:col-[2] outline outline-1 outline-red-500">
            <h1 className='uppercase'>So, you want to travel to
            <span className='font-Bellefair block text-[5rem] my-4 font-extralight text-white'>SPACE</span></h1>
            <p className='text-[15px]  w-full mx-auto max-w-[380px]'>Let’s face it; if you want to go to space, you might as well genuinely go to 
                outer space and not hover kind of on the edge of it. Well sit back, and relax
                because we’ll give you a truly out of this world experience!</p>
        </div>
        {/* image */}
        <div className="md:col-[3] outline outline-1 outline-green-500">
            <a href='/' className='large-btn aspect-[1] text-dark relative z-10 inline-grid place-items-center px-8 bg-white rounded-full after:-z-10 after:absolute after:w-full after:h-full after:bg-white/10 after:rounded-[50%] after:opacity-0 hover:after:opacity-100 focus:after:opacity-100 hover:after:scale-150'>EXPLORE</a>
        </div>
    </section>
  )
}
