export const CrewMemberInfo = ({item, id}) => {
  return (
    <div key={id}>
      <div className="uppercase text-white ">
        {/* crew member role */}
        <h2 className="text-base opacity-[0.4951] mb-2">{item.role}</h2>
        <h1 className="text-2xl">{item.name}</h1>
      </div>
      
      <article>{item.bio}</article>
    </div>
  )
}
