export const DestinationDescription = ({item}) => {
    return (
        <div key={item.id} className="">
            <h1 className='uppercase text-white font-Bellefair text-[56px] mt-5'>{item.name}</h1>
            <p className="font-Barlow leading-[25px] text-[15px] text-[#D0D6F9] w-[23rem] mx-auto ">{item.description}</p>
            <hr className="bg-[#383B4B] my-8 w-[327px] mx-auto" />
            {/* Distance */}
            <div className="">
                <h3>AVG. DISTANCE</h3>
                <h1>{item.distance}</h1>
            </div>
            {/* Travel time */}
            <div className="">
                <h3>EST. TRAVEL TIME</h3>
                <h1>{item.travel}</h1>
            </div>
        </div>
    )
}
