import { BrowserRouter as Router, Route, Routes, NavLink } from 'react-router-dom';
import { useEffect, useState } from "react";
import logo from './components/assets/shared/logo.svg';
import hamburger from './components/assets/shared/icon-hamburger.svg';
import closeIcon from './components/assets/shared/icon-close.svg';
import { Home } from './Pages/Home';
import { Destination } from "./Pages/Destination";
import { Crew } from './Pages/Crew';
import { Technology } from './Pages/Technology';
import AOS from 'aos';
import 'aos/dist/aos.css';
// import { motion } from "framer-motion";

function App() {
  const [menuDisplay, setMenuDisplay] = useState(false);
  AOS.init()
  return (
    <Router>
      <div className="App mt-3 text-accent bg-home-bg-mobile md:bg-home-bg-tablet lg:bg-home-bg-desktop font-Barlow h-full">
        <nav className='flex justify-between items-center py-2 px-5'>
          <img src={logo} alt="logo" className='w-8' />
          {/* menu */}
          <div className={!menuDisplay ? `hidden lg:flex ` : `active bg-menu-bg absolute top-0 left-0 right-0 lg:top-7 lg:right-10 h-screen lg:h-0 z-50 transition bg-blackk`}>
            {/* nav links */}
            <ul className="menu flex flex-col justify-center lg:justify-end lg:flex-row font-medium items-center text-xl text-center min-h-screen lg:min-h-fit gap-6">
              <li id="home" className="relative" >
                <NavLink to="/"><span>00</span> Home </NavLink>
              </li>
              <li id="destination" className="relative ">
                <NavLink to="/destination"><span>01</span> Destination </NavLink>
              </li>
              <li id="crew" className="relative">
                <NavLink to="/crew"><span>02</span> Crew</NavLink>
              </li>
              <li id="technology" className="relative">
                <NavLink to="/technology"><span>03</span> Technology</NavLink>
              </li>
            </ul>

            {/* close button */}
            <i onClick={() => setMenuDisplay(!menuDisplay)} className="ri-close-line lg:hidden absolute top-6 right-4 sm:right-10 cursor-pointer"></i>
          </div>
          {/* hamburger */}
          <div onClick={() => setMenuDisplay(prevValue => !prevValue)} className="flex justify-between flex-col gap-1.5 lg:hidden cursor-pointer rounded-md ">
            <span className="w-6 h-0.5 bg-accent"></span>
            <span className="w-6 h-0.5 bg-accent"></span>
            <span className="w-6 h-0.5 bg-accent"></span>
          </div>
        </nav>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/destination" element={<Destination />} />
          <Route path="/crew" element={<Crew />} />
          <Route path="/technology" element={<Technology />} />
        </Routes>

        {/* <footer className="bg-blackk text-light-grey pb-6 relative w-full bottom-0">
          <hr className="my-4 border-light-grey" />
          <div className="flex justify-between w-40 mx-auto xl:w-48">
            <a href="https://mobile.twitter.com/Chibuike035">
              <i className="ri-twitter-fill text-2xl sm:text-3xl hover:text-white"></i>
            </a>
            <a href="https://github.com/Jay035">
              <i className="ri-github-fill text-2xl sm:text-3xl hover:text-white"></i>
            </a>
            <a href="https://www.linkedin.com/mwlite/in/chibuike-ukandu-8385401b0">
              <i className="ri-linkedin-fill text-2xl sm:text-3xl hover:text-white"></i>
            </a>
            <a href="mailto:codexjay08@gmail.com">
              <i className="ri-mail-line text-2xl sm:text-3xl hover:text-white"></i>
            </a>
          </div>
        </footer> */}
      </div>
    </Router>
  );
}

export default App;
