import React, { useState, useEffect } from 'react';
import { DestinationDescription } from './DestinationDescription';
import { DestinationList } from './DestinationList';
import { moon, mars, europa, titan } from '../data';

export const Destination = () => {
  const [selected, setSelected] = useState("moon");
  const [data, setData] = useState([]);
  const list = [
    {
      id: "moon",
      title: "Moon"
    },
    {
      id: "mars",
      title: "Mars"
    },
    {
      id: "europa",
      title: "Europa"
    },
    {
      id: "titan",
      title: "Titan"
    },
  ]

  useEffect(() => {
    switch(selected){
      case "moon" : 
        setData(moon);
        break;
      case "mars" :
        setData(mars);
        break;
      case "europa" :
        setData(europa);
        break;
      case "titan" :
        setData(titan);
        break;
        default:
        setData(moon)
    }
}, [selected])

  return (
    <section className='relative top-0 bg-destination-bg-mobile md:bg-destination-bg-tablet lg:bg-destination-bg-desktop bg-no-repeat w-full bg-cover overflow-hidden h-screen'>
      <div className="">
        <p><span>01</span> PICK YOUR DESTINATION</p>
        {/* Destination image */}
        <div className="">
          {/* <img src={destinations.images.png} alt="destination img" /> */}
        </div>
        <ul className='uppercase'>
          {
            list.map(item => {
              return(
                <DestinationList 
                  key={item.id}
                  id={item.id} 
                  active={selected === item.id} 
                  setSelected={setSelected} 
                  title={item.title}
                />
              )
            })
          }
        </ul>
        {data.map(item => {
          return(
            <DestinationDescription
              key={item.id}
              id={item.id}
              item={item}
            />
          )
        })}
      </div>
    </section>
  )
}
