import React from 'react'

export const Home = () => {
  return (
    //   home page
    <section className='relative top-28 bg-no-repeat w-full bg-cover overflow-hidden bg-home-bg-mobile md:bg-home-bg-tablet lg:bg-home-bg-desktop md:grid-container gap-4 gap-x-8 h-screen text-center md:text-left tracking-wide grid'>
        {/* details */}
        <div className="md:col-[2] h-">
            <h1 className='uppercase font-Barlow-Condensed tracking-[2.7px]'>So, you want to travel to
            <span className='font-Bellefair block text-[5rem] my-4 leading-[100px] font-extralight text-white'>SPACE</span></h1>
            <p className='text-[15px] leading-6 w-full mx-auto max-w-[380px]'>Let’s face it; if you want to go to space, you might as well genuinely go to 
                outer space and not hover kind of on the edge of it. Well sit back, and relax
                because we’ll give you a truly out of this world experience!</p>
        </div>
        {/* image */}
        <div className="md:col-[3] relative top-[81px]">
            <a href='/' className='large-btn aspect-[1] text-xl text-[#0B0D17] relative z-10 inline-grid place-items-center w-[150px] bg-white rounded-full after:-z-10 after:absolute after:w-full after:h-full after:bg-white/10 after:rounded-[50%] after:opacity-0 hover:after:opacity-100 focus:after:opacity-100 hover:after:scale-150'>EXPLORE</a>
        </div>
    </section>
  )
}
