import { useState, useEffect} from 'react';
import { terminology1, terminology2, terminology3 } from '../data';
import { TechnologyDetails } from './TechnologyDetails';
import { TechnologyImage } from './TechnologyImage';
import { TechnologyList } from './TechnologyList';

export const Technology = () => {
  const [selected, setSelected] = useState("LAUNCH VEHICLE");
  const [data, setData] = useState([]);
  const list = [
    {
      id: 1,
      title: "LAUNCH VEHICLE"
    },
    {
      id: 2,
      title: "SPACEPORT",
    },
    {
      id: 3,
      title: "SPACE CAPSULE",
    }
  ]

  useEffect(() => {
    switch(selected){
      case "LAUNCH VEHICLE" :
        setData(terminology1);
        break;
      case "SPACEPORT" :
        setData(terminology2);
        break;
      case "SPACE CAPSULE" :
        setData(terminology3);
        break;
        default:
        setData(terminology1)
    }
}, [selected])

  return (
    <main className='pt-[88px] md:pt-[8.5rem] transition-all font-Barlow-Condensed text-center bg-technology-bg-mobile md:bg-technology-bg-tablet lg:bg-technology-bg-desktop bg-no-repeat w-full bg-cover bg-center overflow-x-hidden h-screen'>
      <p className='tracking-[2.7px] text-white md:text-xl md:tracking-[3.38px] md:text-left md:pl-[2.4rem]'><span className='opacity-25 text-white mr-[18px]'>03</span> SPACE LAUNCH 101</p>
      <div className="grid lg:grid-container place-items-center md:items-end">
        <section className="mt-8 md:mt-[60px] lg:order-2 lg:col-[3]">
          {
            data.map(img => {
              return(
                <TechnologyImage
                  id={img.id}
                  img={img}
                />
              )
            })
          }
        </section>
        <section className='mt-[34px] md:mt-[56px] lg:order-1 lg:col-[2] lg:flex lg:flex-row lg:text-'>
          <ul className='flex justify-center gap-4 lg:flex-col'>
            {
              list.map( item => {
                return(
                  <TechnologyList 
                    id={item.id} 
                    active={selected === item.title} 
                    setSelected={setSelected} 
                    title={item.title}
                  />
                )
              })
            }
          </ul>
          {
            data.map(item => {
              return(
                <TechnologyDetails
                  id={item.id}
                  item={item}
                />
              )
            })
          }
        </section>
      </div>

    </main>
  )
}
