import React, { useState, useEffect } from 'react';
import { DestinationDescription } from './DestinationDescription';
import { DestinationList } from './DestinationList';
import { destinations } from '../data';

export const Destination = () => {
  const [selected, setSelected] = useState("moon");
  const [data, setData] = useState([]);
  const list = [
    {
      id: "moon",
      title: "Moon"
    },
    {
      id: "mars",
      title: "Mars"
    },
    {
      id: "europa",
      title: "Europa"
    },
    {
      id: "titan",
      title: "Titan"
    },
  ]

  useEffect(() => {
    switch(selected){
        case "moon" : 
            setData(destinations);
            break;
        case "html" :
            setData(HTMLWorks);
            break;
        case "css" :
            setData(VanillaCSSWorks);
            break;
        case "tailwindcss" :
            setData(TailwindCSSWorks);
            break;
        case "vanillaJS" :
            setData(VanillaJSWorks);
            break;
        case "reactJS" :
            setData(ReactJSWorks);
            break;
            default:
                setData(AllWorks)
    }
}, [selected])
  return (
    <section>
        <p><span>01</span> PICK YOUR DESTINATION</p>
        {/* Destination image */}
        <div className=""></div>
        <ul className='uppercase'>
          {list.map( item => {
            return(
              <DestinationList 
                key={item.id}
                id={item.id} 
                active={selected === item.id} 
                setSelected={setSelected} 
                title={item.title} 
              />
            )
          })}
        </ul>
        {data.map(item => {
          return(
            <DestinationDescription 
              key={item.id}
              id={item.id}
              item={item}
            />
          )
        })}

    </section>
  )
}
