import React, { useState, useEffect } from 'react';
import { DestinationDescription } from './DestinationDescription';
import { DestinationList } from './DestinationList';
import { moon, mars, europa, titan } from '../data';
import { DestinationImage } from './DestinationImage';

export const Destination = () => {
  const [selected, setSelected] = useState("moon");
  const [data, setData] = useState([]);
  const list = [
    {
      id: "moon",
      title: "Moon"
    },
    {
      id: "mars",
      title: "Mars"
    },
    {
      id: "europa",
      title: "Europa"
    },
    {
      id: "titan",
      title: "Titan"
    },
  ]

  useEffect(() => {
    switch(selected){
      case "moon" : 
        setData(moon);
        break;
      case "mars" :
        setData(mars);
        break;
      case "europa" :
        setData(europa);
        break;
      case "titan" :
        setData(titan);
        break;
        default:
        setData(moon)
    }
}, [selected])

  return (
    <main className='relative pt-[88px] md:pt-[136px] md:px-10 font-Barlow-Condensed text-center bg-destination-bg-mobile md:bg-destination-bg-tablet lg:bg-destination-bg-desktop bg-no-repeat w-full bg-cover bg-center overflow-x-hidden h-screen lg:text-left tracking-wide gap-8 '>
      <p className='tracking-[2.7px] text-white md:text-left'><span className='opacity-25 text-white mr-[18px]'>01</span> PICK YOUR DESTINATION</p>
      <div className="grid lg:grid-container place-items-center">
        {/* Destination image */}
        <div className="w-[170px] md:w-[300px] mx-auto mt-8 md:mt-[60px]">
          {data.map(item => {
            return(
              <DestinationImage 
                id={item.images.png}
                item={item}
              />
            )
          })}
        </div>
        <div className="">
          <ul className='uppercase text-sm md:text-base tracking-[2.36px] flex justify-center items-center gap-[26px] mt-[26px] md:mt-[53px] mb-5'>
            {
              list.map(item => {
                return(
                  <DestinationList 
                    key={item}
                    id={item.id} 
                    active={selected === item.id} 
                    setSelected={setSelected} 
                    title={item.title}
                  />
                )
              })
            }
          </ul>
          {data.map(item => {
            return(
              <DestinationDescription
                key={item.id}
                id={item.id}
                item={item}
              />
            )
          })}
        </div>
      </div>
    </main>
  )
}
