import { Home } from "./pages/Home";
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { Portfolio } from "./pages/Portfolio";
import { Contact } from "./pages/Contact";
import { ErrorPage } from "./pages/ErrorPage";
import { useState } from "react";
import logo from './components/assets/logo.png';


function App() {
  const [menuClass, setMenuClass] = useState("hidden")

  return (
    <Router>
      <div className="App box-border bg-blackk">
        <nav className="bg-blackk flex justify-between items-center p-6">
          <div className="">
            <img className="w-32" src={logo} alt="logo" />
          </div>
          {/* menu */}
          <div className={` ${menuClass} absolute top-0 left-0 right-0 h-screen z-50 bg-blackk`}>
            {/* links */}
            <ul className="flex flex-col justify-center items-center text-xl text-center min-h-screen gap-6">
              <Link className="text-light-grey text-xl hover:text-white" to="/">Home </Link>
              <Link className="text-light-grey text-xl hover:text-white" to="/portfolio">Portfolio </Link>
              <Link className="text-light-grey text-xl hover:text-white" to="/contact">Contact</Link>
            </ul>

            {/* close button */}
            <i onClick={() => setMenuClass("hidden")} className="ri-close-line text-light-grey hover:text-white text-6xl absolute top-6 right-4 cursor-pointer"></i>

          </div>
          {/* hamburger */}
          <div onClick={() => setMenuClass("")} className="flex justify-between flex-col gap-1.5 cursor-pointer p-3 rounded-md hover:bg-white">
            <span className="justify-self-start w-5 h-0.5 bg-light-grey"></span>
            <span className="w-8 h-0.5 bg-light-grey"></span>
            <span className="ml-auto mr-0 w-5 h-0.5 bg-light-grey"></span>
          </div>

        </nav>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/portfolio" element={<Portfolio />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="*" element={<ErrorPage />} />
        </Routes>
        {/* <Home title="Chibuike Ukandu | CodexJay | Frontend Web Developer" /> */}

        <footer className="bg-blackk text-grey pb-6">
          <hr className="my-4" />
          <div className="flex justify-between w">
            <a href="https://mobile.twitter.com/Chibuike035">
              Twitter
            </a>
            <a href="https://github.com/Jay035">
              Github
            </a>
            <a href="https://www.linkedin.com/mwlite/in/chibuike-ukandu-8385401b0">
              LinkedIn
            </a>
            <a href="mailto:codexjay08@gmail.com">
              Email
            </a>
          </div>
        </footer>
      </div>
    </Router>
  );
}

export default App;
